public class IPhonePhoneFunction {

    public static void turnOn() {
        System.out.println("IPhone ON");
    }

    public static void turnOff() {
        System.out.println("IPhone OF");
    }

    public static void openGMail() {
        System.out.println("IPhone open gmail");
    }

    public static void openVoiceRecord() {
        System.out.println("IPhone voice record");
    }
}
